DROP DATABASE IF EXISTS ElorBase;
CREATE DATABASE ElorBase;
USE ElorBase;

-- Tabla roles
CREATE TABLE roles (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(20) NOT NULL
) ENGINE=InnoDB;

-- Tabla estado de reuniones
CREATE TABLE estado (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(20) NOT NULL
) ENGINE=InnoDB;

-- Tabla ciclos formativos
CREATE TABLE ciclo (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(60) NOT NULL,
    periodo VARCHAR(20) NOT NULL
) ENGINE=InnoDB;

-- Tabla usuarios generales
CREATE TABLE usuario (
    id INT AUTO_INCREMENT PRIMARY KEY,
    login VARCHAR(50) UNIQUE NOT NULL,
    dni VARCHAR(9) UNIQUE NOT NULL,
    clave VARCHAR(255) NOT NULL,
    nombre VARCHAR(30) NOT NULL,
    apellido VARCHAR(15) NOT NULL,
    telefono1 VARCHAR(9),
    telefono2 VARCHAR(9),
    foto longblob,
    direccion VARCHAR(50),
    existe boolean,
    tipo_usuario ENUM('Alumno', 'Profesor') NOT NULL
) ENGINE=InnoDB;

-- Tabla alumnos
CREATE TABLE alumno (
    id INT PRIMARY KEY,
    ciclo_id INT NOT NULL,
    curso INT NOT NULL,
    dual_intensiva BOOLEAN DEFAULT FALSE,
    FOREIGN KEY (id) REFERENCES usuario(id) ON DELETE CASCADE,
    FOREIGN KEY (ciclo_id) REFERENCES ciclo(id) ON DELETE CASCADE
) ENGINE=InnoDB;

-- Tabla profesores
CREATE TABLE profesor (
    id INT PRIMARY KEY,
    tipo ENUM('sustituto', 'contratado') NOT NULL,
    FOREIGN KEY (id) REFERENCES usuario(id) ON DELETE CASCADE
) ENGINE=InnoDB;

-- Tabla asignaturas
CREATE TABLE asignatura (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    horas_totales INT NOT NULL,
    curso INT NOT NULL,
    codigo INT(3) NOT NULL
) ENGINE=InnoDB;

-- Relación asignaturas y ciclos
CREATE TABLE asignatura_ciclo (
    id INT AUTO_INCREMENT PRIMARY KEY,
    asignatura_id INT NOT NULL,
    ciclo_id INT NOT NULL,
    FOREIGN KEY (asignatura_id) REFERENCES asignatura(id) ON DELETE CASCADE,
    FOREIGN KEY (ciclo_id) REFERENCES ciclo(id) ON DELETE CASCADE
) ENGINE=InnoDB;

-- Tabla horarios
CREATE TABLE horario (
    id INT AUTO_INCREMENT PRIMARY KEY,
    dia ENUM('Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes') NOT NULL,
    hora_inicio TIME NOT NULL,
    hora_fin TIME NOT NULL,
    usuario_id INT NOT NULL,
    asignatura_id INT NOT NULL,
    FOREIGN KEY (usuario_id) REFERENCES usuario(id) ON DELETE CASCADE,
    FOREIGN KEY (asignatura_id) REFERENCES asignatura(id) ON DELETE CASCADE
) ENGINE=InnoDB;

-- Tabla reuniones
CREATE TABLE reunion (
    id INT AUTO_INCREMENT PRIMARY KEY,
    titulo VARCHAR(30) NOT NULL,
    asunto VARCHAR(50),
    dia DATE NOT NULL,
    hora TIME NOT NULL,
    aula VARCHAR(40) NOT NULL,
    estado_id INT NOT NULL,
    organizador_id INT NOT NULL,
    FOREIGN KEY (estado_id) REFERENCES estado(id),
    FOREIGN KEY (organizador_id) REFERENCES profesor(id)
) ENGINE=InnoDB;

-- Tabla para invitados a reuniones
CREATE TABLE reunion_invitados (
    id INT AUTO_INCREMENT PRIMARY KEY,
    reunion_id INT NOT NULL,
    invitado_id INT NOT NULL,
    estado_invitacion ENUM('Pendiente', 'Aceptada', 'Rechazada') DEFAULT 'Pendiente',
    FOREIGN KEY (reunion_id) REFERENCES reunion(id) ON DELETE CASCADE,
    FOREIGN KEY (invitado_id) REFERENCES profesor(id) ON DELETE CASCADE
) ENGINE=InnoDB;

-- Relación usuario y roles
CREATE TABLE rol_usuario (
    id INT AUTO_INCREMENT PRIMARY KEY,
    usuario_id INT NOT NULL,
    rol_id INT NOT NULL,
    FOREIGN KEY (usuario_id) REFERENCES usuario(id) ON DELETE CASCADE,
    FOREIGN KEY (rol_id) REFERENCES roles(id) ON DELETE CASCADE
) ENGINE=InnoDB;

